mapper.select:
select * from rss_crawl where 1 = 1 
    and ${condition}  
    and rssCrawlId = #{rssCrawlId}  
    and rssId = #{rssId}  
    and resourceTitle = #{resourceTitle}  
    and resourceUrl = #{resourceUrl}  
    and updateTime = #{updateTime}  
    and resourceDesc = #{resourceDesc}  
    and fingerPrint = #{fingerPrint}

select * from rss_crawl where 1 = 1 
    and ${condition}  
    and rssCrawlId = #{rssCrawlId}  
    and rssId = #{rssId}  
    and resourceTitle = #{resourceTitle}  
    and resourceUrl = #{resourceUrl}  
    and updateTime = #{updateTime}  
    and resourceDesc = #{resourceDesc}  
    and fingerPrint = #{fingerPrint}  
    limit #{startPage},#{page}

select count(rssCrawlId) from rss_crawl where 1 = 1 
    and ${condition}  
    and rssCrawlId = #{rssCrawlId}  
    and rssId = #{rssId}  
    and resourceTitle = #{resourceTitle}  
    and resourceUrl = #{resourceUrl}  
    and updateTime = #{updateTime}  
    and resourceDesc = #{resourceDesc}  
    and fingerPrint = #{fingerPrint}

mapper.delete:
delete from rss_crawl where 1 = 1 
    and ${condition}  
    and rssCrawlId = #{rssCrawlId}  
    and rssId = #{rssId}  
    and resourceTitle = #{resourceTitle}  
    and resourceUrl = #{resourceUrl}  
    and updateTime = #{updateTime}  
    and resourceDesc = #{resourceDesc}  
    and fingerPrint = #{fingerPrint}
