mapper.select:
select * from rss where 1 = 1 
    and rssId = #{rssId}  
    and rssTitle = #{rssTitle}  
    and rssUrl = #{rssUrl}  
    and rssIcon = #{rssIcon}  
    and fingePrint = #{fingePrint}  
    and isSubscribe = #{isSubscribe}  
    and updateTime = #{updateTime}  
    ${condition}

select * from rss where 1 = 1 
    and rssId = #{rssId}  
    and rssTitle = #{rssTitle}  
    and rssUrl = #{rssUrl}  
    and rssIcon = #{rssIcon}  
    and fingePrint = #{fingePrint}  
    and isSubscribe = #{isSubscribe}  
    and updateTime = #{updateTime}  
    ${condition}  
    limit #{startPage},#{page}

select count(rssId) from rss where 1 = 1 
    and rssId = #{rssId}  
    and rssTitle = #{rssTitle}  
    and rssUrl = #{rssUrl}  
    and rssIcon = #{rssIcon}  
    and fingePrint = #{fingePrint}  
    and isSubscribe = #{isSubscribe}  
    and updateTime = #{updateTime}  
    ${condition}

mapper.delete:
delete from rss where 1 = 1 
    and rssId = #{rssId}  
    and rssTitle = #{rssTitle}  
    and rssUrl = #{rssUrl}  
    and rssIcon = #{rssIcon}  
    and fingePrint = #{fingePrint}  
    and isSubscribe = #{isSubscribe}  
    and updateTime = #{updateTime}  
    ${condition}
