mapper.select:
select * from rss where 1 = 1 
    and ${condition}  
    and rssId = #{rssId}  
    and rssTitle = #{rssTitle}  
    and rssUrl = #{rssUrl}  
    and rssIcon = #{rssIcon}

select * from rss where 1 = 1 
    and ${condition}  
    and rssId = #{rssId}  
    and rssTitle = #{rssTitle}  
    and rssUrl = #{rssUrl}  
    and rssIcon = #{rssIcon}  
    limit #{startPage},#{page}

select count(rssId) from rss where 1 = 1 
    and ${condition}  
    and rssId = #{rssId}  
    and rssTitle = #{rssTitle}  
    and rssUrl = #{rssUrl}  
    and rssIcon = #{rssIcon}

mapper.delete:
delete from rss where 1 = 1 
    and ${condition}  
    and rssId = #{rssId}  
    and rssTitle = #{rssTitle}  
    and rssUrl = #{rssUrl}  
    and rssIcon = #{rssIcon}
