mapper.select:
	select * from rss_subscribe where 1 = 1 
    and rssSubscribeId = #{rssSubscribeId}   and rssId = #{rssId}   and rssTypeId = #{rssTypeId}  
		 ${condition}

	select * from rss_subscribe where 1 = 1 
    and rssSubscribeId = #{rssSubscribeId}   and rssId = #{rssId}   and rssTypeId = #{rssTypeId}  
		 ${condition} 
         limit #{startPage},#{page}

		select count(rssSubscribeId) from rss_subscribe where 1 = 1 
    and rssSubscribeId = #{rssSubscribeId}  and rssId = #{rssId}  and rssTypeId = #{rssTypeId}  
		 ${condition}

mapper.update:
update rss_subscribe 
  
rssSubscribeId = #{rssSubscribeId},rssId = #{rssId},rssTypeId = #{rssTypeId},

where 1 = 1  and rssSubscribeId = #{rssSubscribeId} 
 ${condition}

mapper.delete:
		delete from rss_subscribe where 1 = 1 
    and rssSubscribeId = #{rssSubscribeId}   and rssId = #{rssId}   and rssTypeId = #{rssTypeId}  
		 ${condition}
